import { useState, useEffect } from "react";
import "./LoginComponent.css";
import Navbar from "./Navbar";
import "./MainPageComponent.css";
import "./Profile.css";
import job from "../Images/profile-job.png";
import study from "../Images/profile-study.png";
import home from "../Images/profile-home.png";

function EditPage() {
  let history = useHistory();
  const [intro, setIntro] = useState(
    "This is the old intro but just a part of it :)"
  );

  const [jobTitle, setJobTitle] = useState("Director at 99media Ltd");

  const [education, setEducation] = useState(
    "Technical University of Eindhoven"
  );

  const [livingCountry, setLivingCountry] = useState("Netherlands");

  const [livingCity, setLivingCity] = useState("Eindhoven");

  const handleIntro = (event) => {
    setIntro(event.target.value);
  };

  const handleJobTitle = (event) => {
    setJobTitle(event.target.value);
  };

  const handleEducation = (event) => {
    setEducation(event.target.value);
  };

  const handleLivingCountry = (event) => {
    setLivingCountry(event.target.value);
  };

  const handleLivingCity = (event) => {
    setLivingCity(event.target.value);
  };

  const update = () => {
    history.push("/profile");
  };
  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="box">
          <form className="login-form">
            <div className="field">
              <h3>Intro</h3>
              <input
                id="intro"
                type="text"
                value={intro}
                onChange={handleIntro}
              />
              <label for="intro">Intro</label>
            </div>
          </form>
        </div>
        <div className="box">
          <div className="heading">
            <picture id="login_logo"></picture>
          </div>

          <form className="login-form">
            <div className="field">
              <h3>
                <img src={job} width="20px" height="20px" /> Job Title
              </h3>

              <input
                id="username"
                type="name"
                value={jobTitle}
                onChange={handleJobTitle}
              />
              <label for="username">Job</label>
            </div>
            <br />
            <div className="field">
              <h3>
                <img src={study} width="20px" height="20px" /> Education
              </h3>

              <input
                id="education"
                type="text"
                value={education}
                onChange={handleEducation}
              />
              <label for="education">Education</label>
            </div>
            <br />
            <div className="field">
              <h3>
                <img src={home} width="20px" height="20px" /> Living Country
              </h3>

              <input
                id="livingCountry"
                type="text"
                value={livingCountry}
                onChange={handleLivingCountry}
              />
              <label for="livingCountry">Living Country</label>
            </div>
            <br />
            <div className="field">
              <h3>
                <img src={home} width="20px" height="20px" /> Living City
              </h3>

              <input
                id="livingCity"
                type="text"
                value={livingCity}
                onChange={handleLivingCity}
              />
              <label for="livingCity">Living City</label>
            </div>
            <br />

            <button className="login-button" title="login">
              Update
            </button>
            <div className="separator">
              <div className="line"></div>
              <p>OR</p>
              <div className="line"></div>
            </div>
            <div className="other">
              <button className="fb-login-btn" type="button" onClick={update}>
                <i className="fa fa-facebook-official fb-icon"></i>
                <span className="">Log in with Facebook</span>
              </button>
              <a className="forgot-password" href="#">
                Forgot password?
              </a>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default EditPage;
