import React from "react";
import "./LoginComponent.css";
import logo from "../logo.png";
import ParticlesBg from "particles-bg";

class LoginComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { username: "", password: "" };
    this.handleUsername = this.handleUsername.bind(this);
    this.handlePassword = this.handlePassword.bind(this);
    this.login = this.login.bind(this);
  }

  handleUsername(event) {
    this.setState({
      username: event.target.value,
    });
  }

  handlePassword(event) {
    this.setState({
      password: event.target.value,
    });
  }

  componentDidMount() {
    localStorage.clear();
  }

  login(e) {
    e.preventDefault();
    if (this.state.username.trim() && this.state.password.trim()) {
      let request = {
        username: this.state.username,
        password: this.state.password,
      };
      try {
        AuthenticationService.authentication(request)
          .then((res) => {
            localStorage.setItem("token", res.data.Authorization);
            localStorage.setItem("username", this.state.username);
            this.props.history.push("/concerts");
            window.location.reload(false);
          })
          .catch((error) => {
            return error;
          });
      } catch (error) {
        console.log(error);
      }
    } else {
      console.log("Were null");
    }
  }
  render() {
    return (
      <div className="container">
        <div className="box">
          <div className="heading">
            <picture id="login_logo">
              <img src={logo} />
            </picture>
          </div>
          <form className="login-form">
            <div className="field">
              <input
                id="username"
                type="name"
                placeholder="Phone number, username, or email"
              />
              <label for="username">Phone number, username, or email</label>
            </div>
            <div className="field">
              <input id="password" type="password" placeholder="password" />
              <label for="password">Password</label>
            </div>
            <button className="login-button" title="login" onClick={this.login}>
              Log In
            </button>
            <div className="separator">
              <div className="line"></div>
              <p>OR</p>
              <div className="line"></div>
            </div>
            <div className="other">
              <button className="fb-login-btn" type="button">
                <i className="fa fa-facebook-official fb-icon"></i>
                <span className="">Log in with Facebook</span>
              </button>
              <a className="forgot-password" href="#">
                Forgot password?
              </a>
            </div>
          </form>
        </div>
        <div className="box">
          <p>
            Don't have an account?{" "}
            <a className="signup" href="http://localhost:3000/ping/register">
              Sign Up
            </a>
          </p>
        </div>
        <ParticlesBg type="circle" bg={true} />
      </div>
    );
  }
}

export default LoginComponent;
