import axios from "axios";

const CONCERTAPIBASEURL = "http://localhost:5020/api/follow";

class FollowService {
  getFolowing(username) {
    return axios.get(CONCERTAPIBASEURL + "/followed/" + username);
  }

  getFollower(username) {
    return axios.get(CONCERTAPIBASEURL + "/followers/" + username);
  }

  getRequests(username) {
    return axios.get(CONCERTAPIBASEURL + "/requests/" + username);
  }

  async createRequest(body) {
    return await axios.post(CONCERTAPIBASEURL, body);
  }
}

export default new FollowService();
