import React from "react";
import Navbar from "./Navbar";
import "./AdminPanel.css";
import {
  BsFillPeopleFill,
  BsSearch,
  BsFillGrid3X3GapFill,
} from "react-icons/bs";
import Avatar from "react-user-avatar";
import ProfileService from "../Services/ProfileService";

class AdminPanel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      profiles: [],
      searchWord: "",
    };
    this.handleSearchWord = this.handleSearchWord.bind(this);
    this.filter = this.filter.bind(this);
    this.updateBlock = this.updateBlock.bind(this);
    this.updateRole = this.updateRole.bind(this);
  }
  componentDidMount() {
    ProfileService.getProfiles().then((res) => {
      this.setState({ profiles: res.data });
    });
  }

  handleSearchWord(event) {
    this.setState({
      searchWord: event.target.value,
    });
  }

  updateBlock(block) {
    console.log("Block is " + block);
  }

  updateRole(role) {
    console.log("Role is " + role);
  }

  filter() {
    const keyword = this.state.searchWord;

    if (keyword !== "") {
      const results = this.state.profiles.filter((user) => {
        return user.username.toLowerCase().startsWith(keyword.toLowerCase());
      });
      return results;
    } else {
      return this.state.profiles;
    }
  }

  render() {
    return (
      <div id="admin-div">
        <Navbar />
        <div className="main-content-admin">
          <header id="header">
            <h2>
              <span>
                <BsFillGrid3X3GapFill />
              </span>{" "}
              Dashboard
            </h2>
            <div className="search-wrapper">
              <span>
                <BsSearch color="grey" />
              </span>
              <input
                type="search"
                placeholder="Search here. . ."
                value={this.state.searchWord}
                onChange={this.handleSearchWord}
              />
            </div>
            <div className="user-wrapper">
              <div>
                <h4></h4>
                <small></small>
              </div>
            </div>
          </header>
        </div>

        <main id="admin-main">
          <div className="cards">
            <div className="card-single">
              <div>
                <h1>{this.state.profiles.length}</h1>
                <span>Total Users</span>
              </div>
              <div>
                <span className="las la-users">
                  <BsFillPeopleFill />
                </span>
              </div>
            </div>
            <div className="card-single">
              <div>
                <h1>
                  {
                    this.state.profiles.filter((p) => p.isPublic == false)
                      .length
                  }
                </h1>
                <span>Private Users</span>
              </div>
              <div>
                <span className="las la-users">
                  {" "}
                  <BsFillPeopleFill />
                </span>
              </div>
            </div>
            <div className="card-single">
              <div>
                <h1>
                  {this.state.profiles.filter((p) => p.isPublic == true).length}
                </h1>
                <span>Public Profiles</span>
              </div>
              <div>
                <span className="las la-users">
                  {" "}
                  <BsFillPeopleFill />
                </span>
              </div>
            </div>
            <div className="card-single">
              <div>
                <h1>
                  {" "}
                  {
                    this.state.profiles.filter((p) => p.isBlocked == true)
                      .length
                  }
                </h1>
                <span>Blocked Profiles</span>
              </div>
              <div>
                <span className="las la-users">
                  {" "}
                  <BsFillPeopleFill />
                </span>
              </div>
            </div>
          </div>
          <div className="recent-grid">
            <div className="projects">
              <div className="card-admin">
                <div className="card-header">
                  <h3>Accounts Overview</h3>
                  <button>See all</button>
                </div>
                <div className="card-body">
                  <div className="table-responsive">
                    <table>
                      <thead>
                        <tr>
                          <td>Username</td>
                          <td>Status</td>
                          <td>Role</td>
                        </tr>
                      </thead>
                      <tbody>
                        {this.filter().map((p) => (
                          <tr keu={p.id}>
                            <td>{p.username}</td>
                            {p.isBlocked == true && <td>Blocked</td>}
                            {p.isBlocked == false && <td>Not Blocked</td>}
                            <td>
                              {p.role == "user" && (
                                <span className="status pink"></span>
                              )}
                              {p.role != "user" && (
                                <span className="status orange"></span>
                              )}
                              {p.role}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
                <div className="bard-body">
                  <div className="table-responsive"></div>
                </div>
              </div>
            </div>
            <div className="customers">
              <div className="card-admin">
                <div className="card-header">
                  <h3>Actions</h3>
                  <button>See all</button>
                </div>
                {this.filter().map((p) => (
                  <div className="card-body" key={p.id}>
                    <div className="customer">
                      <div className="info">
                        <Avatar
                          name={p.username}
                          size="40px"
                          width="40px"
                          height="40px"
                          className="info-img"
                        />
                        <div className="contact">
                          <h4>{p.username}</h4>
                          {p.isPublic == false && <small>Private</small>}
                          {p.isPublic == true && <small>Public</small>}
                        </div>
                      </div>
                      <div className="info-actions">
                        {p.role == "user" && (
                          <span onClick={this.updateRole("admin", p)}>
                            Make Admin
                          </span>
                        )}
                        {p.role != "user" && (
                          <span onClick={this.updateRole("user", p)}>
                            Make User
                          </span>
                        )}
                        {p.isBlocked == false && (
                          <span onClick={this.updateBlock(true, p)}>Block</span>
                        )}
                        {p.isBlocked == true && (
                          <span onClick={this.updateBlock(false, p)}>
                            Unblock
                          </span>
                        )}
                        <span>Delete</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </main>
      </div>
    );
  }
}

export default AdminPanel;
