import React from "react";
import { useState, useEffect } from "react";
import "./LoginComponent.css";
import Navbar from "./Navbar";
import "./MainPageComponent.css";
import "./Profile.css";
import { useHistory } from "react-router-dom";
import job from "../Images/profile-job.png";
import study from "../Images/profile-study.png";
import birth from "../Images/birthday.png";
import home from "../Images/profile-home.png";
import origin from "../Images/profile-location.png";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import ProfileService from "../Services/ProfileService";
import { BsEyeFill, BsEyeSlashFill } from "react-icons/bs";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

/*class EditPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      intro: "",
      jobTitle: "",
      profile: "",
      birthday: "",
      education: "",
      livingCountry: "",
      livingCity: "",
      isPubic: "",
      originCity: "",
      originCountry: "",
    };
    this.handleIntro = this.handleIntro.bind(this);
    this.handleBirthday = this.handleBirthday.bind(this);
    this.handleJobTitle = this.handleJobTitle.bind(this);
    this.handleEducation = this.handleEducation.bind(this);
    this.handleLivingCountry = this.handleLivingCountry.bind(this);
    this.handleLivingCity = this.handleLivingCity.bind(this);
    this.handleOriginCountry = this.handleOriginCountry.bind(this);
    this.handleOriginCity = this.handleOriginCity.bind(this);
    this.handleIsPublic = this.handleIsPublic.bind(this);
    this.update = this.update.bind(this);
  }

  handleIntro(event) {
    this.setState({
      intro: event.target.value,
    });
  }
  handleJobTitle(event) {
    this.setState({
      jobTitle: event.target.value,
    });
  }
  handleBirthday(event) {
    this.setState({
      birthday: event.target.value,
    });
  }
  handleEducation(event) {
    this.setState({
      education: event.target.value,
    });
  }
  handleLivingCountry(event) {
    this.setState({
      livingCountry: event.target.value,
    });
  }
  handleLivingCity(event) {
    this.setState({
      livingCity: event.target.value,
    });
  }
  handleOriginCountry(event) {
    this.setState({
      originCountry: event.target.value,
    });
  }
  handleOriginCity(event) {
    this.setState({
      originCity: event.target.value,
    });
  }
  handleIsPublic(event) {
    this.setState({
      isPubic: event.target.value,
    });
  }

  componentDidMount() {
    ProfileService.getProfile(localStorage.getItem("username")).then((res) => {
      this.setState({
        profile: res.data,
        intro: res.data.intro,
        birthday: res.data.birthday,
        education: res.data.education,
        isPubic: res.data.isPubic,
        jobTitle: res.data.job,
        livingCity: res.data.livingCity,
        livingCountry: res.data.livingCountry,
        originCity: res.data.originCityy,
        originCountry: res.data.originCountry,
      });
    });
  }

  update(e) {
    //e.preventDefault();
    //history.push("/profile/" + localStorage.getItem("username"));
    //window.location.reload(false);
    e.preventDefault();
    let body = {
      id: this.state.profile.id,
      firstName: this.state.profile.firstName,
      lastName: this.state.profile.lastName,
      imageUrl: this.state.profile.imageUrl,
      intro: this.state.intro,
      role: this.state.profile.role,
      job: this.state.jobTitle,
      education: this.state.education,
      isPubic: this.state.isPubic,
      isBlocked: this.state.profile.isBlocked,
      livingCity: this.state.livingCity,
      livingCountry: this.state.livingCountry,
      originCityy: this.state.originCity,
      originCountry: this.state.originCountry,
      birthday: this.state.birthday,
    };
    console.log(body);

    try {
      ProfileService.updateProfile(body)
        .then(() => {
          this.props.history.push(
            "/profile/" + localStorage.getItem("username")
          );
          window.location.reload(false);
        })
        .catch((error) => {
          console.log(error);
          confirmAlert({
            customUI: ({ onClose }) => {
              return (
                <div className="custom-ui">
                  <h1>Something went wrong!</h1>
                  <p>Please try again</p>
                  <button className="button-1" onClick={onClose}>
                    Close
                  </button>
                </div>
              );
            },
          });
        });
    } catch (error) {
      confirmAlert({
        customUI: ({ onClose }) => {
          return (
            <div className="custom-ui">
              <h1>Something went wrong!</h1>
              <p>Please try again</p>
              <button className="button-1" onClick={onClose}>
                Close
              </button>
            </div>
          );
        },
      });
    }
  }

  render() {
    return (
      <div>
        <Navbar />
        <div className="container">
          <div className="box">
            <form className="login-form">
              <div className="field">
                <h3>Intro</h3>
                <input
                  id="intro"
                  type="text"
                  value={this.state.intro}
                  onChange={this.handleIntro}
                />
                <label for="intro">Intro</label>
              </div>
            </form>
          </div>
          <div className="box">
            <div className="heading">
              <picture id="login_logo"></picture>
            </div>

            <form className="login-form">
              <div className="field">
                <h3>
                  <img src={job} width="20px" height="20px" /> Job Title
                </h3>

                <input
                  id="job"
                  type="name"
                  value={this.state.jobTitle}
                  onChange={this.handleJobTitle}
                />
                <label for="job">Job</label>
              </div>
              <br />
              <div className="field">
                <h3>
                  <img src={birth} width="20px" height="20px" /> Birthday
                </h3>

                <DatePicker
                  selected={this.state.birthday}
                  value={this.state.birthday}
                  onChange={this.setBirthday}
                  className="form-control"
                  name="dateTime"
                  dateFormat="yyyy/MM/dd"
                />
              </div>
              <br />
              <div className="field">
                <h3>
                  <img src={study} width="20px" height="20px" /> Education
                </h3>

                <input
                  id="education"
                  type="text"
                  value={this.state.education}
                  onChange={this.handleEducation}
                />
                <label for="education">Education</label>
              </div>
              <br />
              <div className="field">
                <h3>
                  <img src={home} width="20px" height="20px" /> Living Country
                </h3>

                <input
                  id="livingCountry"
                  type="text"
                  value={this.state.livingCountry}
                  onChange={this.handleLivingCountry}
                />
                <label for="livingCountry">Living Country</label>
              </div>
              <br />
              <div className="field">
                <h3>
                  <img src={home} width="20px" height="20px" /> Living City
                </h3>

                <input
                  id="livingCity"
                  type="text"
                  value={this.state.livingCity}
                  onChange={this.handleLivingCity}
                />
                <label for="livingCity">Living City</label>
              </div>
              <br />
              <div className="field">
                <h3>
                  <img src={origin} width="20px" height="20px" /> Origin Country
                </h3>

                <input
                  id="originCountry"
                  type="text"
                  value={this.state.originCountry}
                  onChange={this.handleOriginCountry}
                />
                <label for="livingCountry">Origin Country</label>
              </div>
              <br />
              <div className="field">
                <h3>
                  <img src={origin} width="20px" height="20px" /> Origin City
                </h3>

                <input
                  id="originCity"
                  type="text"
                  value={this.state.originCity}
                  onChange={this.handleOriginCity}
                />
                <label for="originCity">Origin City</label>
              </div>

              <div className="separator">
                <div className="line"></div>
                <p>Privacy</p>
                <div className="line"></div>
              </div>
              <div className="other">
                <button className="fb-login-btn" type="button">
                  {"    "}
                  {this.state.isPubic == true && (
                    <BsEyeFill size="17px" onClick={this.handleIsPublic} />
                  )}
                  {this.state.isPubic == false && (
                    <BsEyeSlashFill size="17px" onClick={this.handleIsPublic} />
                  )}
                  {"    "}
                  {this.state.isPubic == true && (
                    <span className="">Public Account</span>
                  )}
                  {this.state.isPubic == false && (
                    <span className="">Private Account</span>
                  )}
                </button>
                {this.state.isPubic == true && (
                  <a className="forgot-password">
                    Everyone can view your account
                  </a>
                )}
                {this.state.isPubic == false && (
                  <a className="forgot-password">
                    Only people following you can view your account
                  </a>
                )}
              </div>
              <br />
              <br />
              <button
                className="login-button"
                title="login"
                onClick={this.update}
              >
                Update
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
