import React from "react";
import "./MainPageComponent.css";
import Avatar from "react-user-avatar";
import TagsInput from "react-tagsinput";
import "react-tagsinput/react-tagsinput.css";
import feeling from "../Images/feeling.png";

class WritePostComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchWord: "",
      firstName: "Axl",
      lastName: "Rose",
      pings: [],
      followers: [],
      following: [1, 2, 3, 4, 5, 6, 7, 8, 9],
      trends: [],
      tags: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(tags) {
    this.setState({ tags });
  }

  componentDidMount() {}

  render() {
    return (
      <div className="write-post-container">
        <div className="user-profile">
          <div className="user-profile-img">
            <Avatar size="40" name={this.state.firstName} src="" />
          </div>

          <div>
            <p>
              {this.state.firstName} {this.state.lastName}
            </p>
            <small>Public</small>
            <p></p>
          </div>
        </div>

        <div className="post-input-container">
          <textarea rows="3" placeholder={"What's on your mind?"}></textarea>
          <TagsInput
            color="blue"
            size="20"
            value={this.state.tags}
            onChange={this.handleChange}
          />
          <div className="add-post-links">
            <a href="#">Hashtags</a>

            <a>
              <img src={feeling} /> Emojis
            </a>
            <a href="#">Mentions</a>
          </div>
        </div>
      </div>
    );
  }
}

export default WritePostComponent;
