import React from "react";
import { useState, useEffect } from "react";
import "./LoginComponent.css";
import Navbar from "./Navbar";
import "./MainPageComponent.css";
import "./Profile.css";
import { useHistory } from "react-router-dom";
import job from "../Images/profile-job.png";
import study from "../Images/profile-study.png";
import birth from "../Images/birthday.png";
import home from "../Images/profile-home.png";
import origin from "../Images/profile-location.png";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import ProfileService from "../Services/ProfileService";
import { BsEyeFill, BsEyeSlashFill } from "react-icons/bs";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

class EditPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      intro: "",
      jobTitle: "",
      profile: "",
      birthday: "",
      education: "",
      livingCountry: "",
      livingCity: "",
      isPubic: "",
      originCity: "",
      originCountry: "",
    };
    this.handleIntro = this.handleIntro.bind(this);
    this.handleBirthday = this.handleBirthday.bind(this);
    this.handleJobTitle = this.handleJobTitle.bind(this);
    this.handleEducation = this.handleEducation.bind(this);
    this.handleLivingCountry = this.handleLivingCountry.bind(this);
    this.handleLivingCity = this.handleLivingCity.bind(this);
    this.handleOriginCountry = this.handleOriginCountry.bind(this);
    this.handleOriginCity = this.handleOriginCity.bind(this);
    this.handleIsPublic = this.handleIsPublic.bind(this);
  }

  handleIntro(event) {
    this.setState({
      intro: event.target.value,
    });
  }
  handleJobTitle(event) {
    this.setState({
      jobTitle: event.target.value,
    });
  }
  handleBirthday(event) {
    this.setState({
      birthday: event.target.value,
    });
  }
  handleEducation(event) {
    this.setState({
      education: event.target.value,
    });
  }
  handleLivingCountry(event) {
    this.setState({
      livingCountry: event.target.value,
    });
  }
  handleLivingCity(event) {
    this.setState({
      livingCity: event.target.value,
    });
  }
  handleOriginCountry(event) {
    this.setState({
      originCountry: event.target.value,
    });
  }
  handleOriginCity(event) {
    this.setState({
      originCity: event.target.value,
    });
  }
  handleIsPublic(event) {
    this.setState({
      isPubic: event.target.value,
    });
  }

  componentDidMount() {
    ProfileService.getProfile(localStorage.getItem("username")).then((res) => {
      this.setState({
        profile: res.data,
        intro: res.data.intro,
        birthday: res.data.birthday,
        education: res.data.education,
        isPubic: res.data.isPubic,
        jobTitle: res.data.job,
        livingCity: res.data.livingCity,
        livingCountry: res.data.livingCountry,
        originCity: res.data.originCityy,
        originCountry: res.data.originCountry,
      });
    });
  }

  update = (e) => {
    //e.preventDefault();
    //history.push("/profile/" + localStorage.getItem("username"));
    //window.location.reload(false);
    e.preventDefault();
    let body = {
      id: this.state.profile.id,
      firstName: this.state.profile.firstName,
      lastName: this.state.profile.lastName,
      imageUrl: this.state.profile.imageUrl,
      intro: this.state.intro,
      role: this.state.profile.role,
      job: this.state.jobTitle,
      education: this.state.education,
      isPubic: this.state.isPubic,
      isBlocked: this.state.profile.isBlocked,
      livingCity: this.state.livingCity,
      livingCountry: this.state.livingCountry,
      originCityy: this.state.originCity,
      originCountry: this.state.originCountry,
      birthday: this.state.birthday,
    };
    console.log(body);

    try {
      ProfileService.updateProfile(body)
        .then(() => {
          this.props.history.push(
            "/profile/" + localStorage.getItem("username")
          );
          //window.location.reload(false);
        })
        .catch((error) => {
          console.log(error);
          confirmAlert({
            customUI: ({ onClose }) => {
              return (
                <div className="custom-ui">
                  <h1>Something went wrong!</h1>
                  <p>Please try again</p>
                  <button className="button-1" onClick={onClose}>
                    Close
                  </button>
                </div>
              );
            },
          });
        });
    } catch (error) {
      confirmAlert({
        customUI: ({ onClose }) => {
          return (
            <div className="custom-ui">
              <h1>Something went wrong!</h1>
              <p>Please try again</p>
              <button className="button-1" onClick={onClose}>
                Close
              </button>
            </div>
          );
        },
      });
    }
  };
}

