import axios from "axios";

const CONCERTAPIBASEURL = "http://localhost:5020/api/post";

class PostService {
  getPostsByUsername(username) {
    return axios.get(CONCERTAPIBASEURL + "/" + username);
  }

  getPostById(id) {
    return axios.get(CONCERTAPIBASEURL + "/id/" + id);
  }

  createPost(body) {
    return axios.post(CONCERTAPIBASEURL, body);
  }
}

export default new PostService();
