import React from "react";
import "./MainPageComponent.css";
import Avatar from "react-user-avatar";

class PostComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchWord: "",
      firstName: "Axl",
      lastName: "Rose",
      pings: [],
      followers: [],
      following: [1, 2, 3, 4, 5, 6, 7, 8, 9],
      trends: [],
    };
  }

  componentDidMount() {}

  render() {
    return (
      <div className="post-container" id="profile-post-container">
        <div className="post-row">
          <div className="user-profile">
            <div className="user-profile-img">
              <Avatar size="40" name={this.state.lastName} src="" />
            </div>
            <div>
              <p>
                {this.state.firstName} {this.state.lastName}
              </p>
              <span>June 24 2021, 13:40 pm</span>
              <p></p>
            </div>
          </div>
          <a href="#">
            <BsThreeDotsVertical />
          </a>
        </div>

        <p className="post-text">
          Fake Post massage from one of the accounts that I follow! Here is a
          hashtag #lovethiswebsite
        </p>
        <div className="post-row">
          <div className="activity-icons">
            <div>
              <img src={like}></img>120
            </div>
          </div>
          <div className="post-profile-icon"></div>
        </div>
      </div>
    );
  }
}

export default PostComponent;
