import React from "react";
import "./MainPageComponent.css";
import "./Profile.css";
import Avatar from "react-user-avatar";
import job from "../Images/profile-job.png";
import study from "../Images/profile-study.png";
import birthday from "../Images/birthday.png";
import home from "../Images/profile-home.png";
import origin from "../Images/profile-location.png";
import ProfileService from "../Services/ProfileService";
import FollowService from "../Services/FollowService";

class ProfileDetails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchWord: "",
      pings: [],
      followers: [],
      following: this.props.followingData,
      trends: [],
      profile: this.props.profileData,
    };
  }

  componentDidMount() {
    /* ProfileService.getProfile().then((res) => {
      this.setState({ profile: res.data });
      console.log(res.data);
    });

    FollowService.getFolowing(this.state.username).then((res) => {
      this.setState({ following: res.data });
    });*/
    //console.log("Profile" + this.state.profile);
    //console.log("Following" + this.state.following);
  }

  render() {
    return (
      <div>
        <div className="profile-intro">
          <h3>Intro</h3>
          <p className="intro-text">{this.state.profile.intro}</p>
          <hr />
          <ul>
            <li>
              <img src={job} />
              {this.state.profile.job}
            </li>
            <li>
              <img src={study} />
              Studied at {this.state.profile.education}
            </li>
            <li>
              <img src={birthday} />
              Born in {this.state.profile.birthday}
            </li>
            <li>
              <img src={home} />
              Lives in {this.state.profile.livingCity},{" "}
              {this.state.profile.livingCountry}
            </li>
            <li>
              <img src={origin} />
              From {this.state.profile.originCityy},{" "}
              {this.state.profile.originCountry}
            </li>
          </ul>
        </div>

        <div className="profile-intro">
          <div className="title-box">
            <h3>Photos</h3>
            <a href="#">All Photos</a>
          </div>
          <div className="photo-box">
            <div>
              <img src="" />
            </div>
            <div>
              <img src="" />
            </div>
            <div>
              <img src="" />
            </div>
            <div>
              <img src="" />
            </div>
            <div>
              <img src="" />
            </div>
            <div>
              <img src="" />
            </div>
          </div>
        </div>
        <div className="profile-intro">
          <div className="title-box">
            <h3>Friends</h3>
            <a href="#">All Friends</a>
          </div>
          <div className="photo-box">
            {this.state.following.slice(0, 6).map((f) => (
              <Avatar
                size="40"
                name={this.state.firstName}
                src=""
                className="fr-image"
              />
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default ProfileDetails;
