import React from "react";
import "./MainPageComponent.css";
import Navbar from "./Navbar";
import "./Profile.css";
import Avatar from "react-user-avatar";
import SquareAvatar from "react-avatar";
import cover from "../cover.png";
import addFriends from "../Images/add-friends.png";
import message from "../Images/message.png";
import more from "../Images/more.png";
import arrow from "../Images/arrow.png";
import WritePostComponent from "./WritePostComponent";
import PostComponent from "./PostComponent";
import ProfileDetails from "./ProfileDetails";
import ProfileService from "../Services/ProfileService";
import FollowService from "../Services/FollowService";
import { BsEyeSlashFill } from "react-icons/bs";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchWord: "",
      profile: "",
      pings: [],
      followers: [],
      following: [],
      username: localStorage.getItem("username"),
    };
    this.edit = this.edit.bind(this);
  }

  componentDidMount() {
    //retrieve user's profile
    ProfileService.getProfile(this.state.username).then((res) => {
      this.setState({ profile: res.data });
      console.log(res.data);
      console.log(this.state.profile.isPublic);
    });

    //retrieve user's followings
    FollowService.getFolowing(this.state.username).then((res) => {
      this.setState({ following: res.data });
    });

    //retrieve user's followings
    FollowService.getFollower(this.state.username).then((res) => {
      this.setState({ followers: res.data });
    });
  }

  edit(e) {
    e.preventDefault();
    this.props.history.push("/edit");
    window.location.reload(false);
  }

  render() {
    return (
      <div id="main_page_all">
        <body id="main_page_body">
          <Navbar />

          <div className="profile-container">
            <img className="cover-img" src={cover} />
            <div className="profile-details">
              <div className="pd-left">
                <div className="pd-row">
                  <Avatar
                    size="100"
                    name={this.state.username}
                    src=""
                    className="pd-image"
                  />
                  <div className="pd-overview">
                    <h3>{this.state.username}</h3>
                    <p>{this.state.following.length} Friends - following</p>
                    {this.state.following.slice(0, 4).map((following) => (
                      <SquareAvatar
                        size="25"
                        name={this.state.username}
                        src=""
                        className="fr-image"
                      />
                    ))}
                  </div>
                </div>
              </div>
              {this.state.username == localStorage.getItem("username") && (
                <div className="pd-right">
                  <button type="button">
                    <img src={addFriends} />
                    Public
                  </button>
                  <button type="button" onClick={this.edit}>
                    <img src={message} />
                    Edit
                  </button>
                  <br />
                  <a href="">
                    <img src={more} />
                  </a>
                </div>
              )}
              {this.state.username != localStorage.getItem("username") && (
                <div className="pd-right">
                  <button type="button">
                    <img src={addFriends} />
                    Friends
                  </button>
                  <br />
                  <a href="">
                    <img src={more} />
                  </a>
                </div>
              )}
            </div>
            {(this.state.profile.isPublic == true ||
              this.state.username == localStorage.getItem("username")) && (
              <div className="profile-info">
                <div className="info-col">
                  <ProfileDetails />
                </div>
                {this.state.username == localStorage.getItem("username") && (
                  <div className="post-col">
                    <WritePostComponent />
                    {this.state.pings.map((pings) => (
                      <PostComponent />
                    ))}
                    {this.state.pings.length == 0 && (
                      <>
                        <br /> <br />
                        <button type="button" className="load-more-btn">
                          No posts yet
                        </button>
                      </>
                    )}
                  </div>
                )}
                {this.state.username != localStorage.getItem("username") && (
                  <div className="post-col">
                    {this.state.pings.map((pings) => (
                      <PostComponent />
                    ))}
                    {this.state.pings.length == 0 && (
                      <>
                        <br /> <br />
                        <button type="button" className="load-more-btn">
                          No posts yet
                        </button>
                      </>
                    )}
                  </div>
                )}
              </div>
            )}
            {this.state.profile.isPublic == false &&
              this.state.username != localStorage.getItem("username") && (
                <>
                  <br /> <br />
                  <button type="button" className="load-more-btn">
                    <BsEyeSlashFill /> Private
                  </button>
                </>
              )}
          </div>
        </body>
      </div>
    );
  }
}

export default Profile;
