import React from "react";
import "./MainPageComponent.css";
import Avatar from "react-user-avatar";
import TagsInput from "react-tagsinput";
import "react-tagsinput/react-tagsinput.css";
import Select from "react-select";
import feeling from "../Images/feeling.png";

class WritePostComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchWord: "",
      firstName: "Axl",
      lastName: "Rose",
      username: localStorage.getItem("username"),
      pings: [],
      followers: [],
      following: [
        { value: "chocolate", label: "Chocolate" },
        { value: "strawberry", label: "Strawberry" },
        { value: "vanilla", label: "Vanilla" },
        { value: "vanilla1", label: "Vanilla1" },
        { value: "vanilla2", label: "Vanilla2" },
        { value: "vanilla3", label: "Vanilla3" },
      ],
      trends: [],
      mentions: [],
      tags: [],
    };
    this.handleChangeTags = this.handleChangeTags.bind(this);
    this.handleChangeMentions = this.handleChangeMentions.bind(this);
    this.createPost = this.createPost.bind(this);
  }

  handleChangeTags(tags) {
    this.setState({ tags });
  }

  handleChangeMentions(mentions) {
    this.setState({ mentions });
  }

  createPost(post) {}

  componentDidMount() {}

  render() {
    return (
      <div className="write-post-container">
        <div className="user-profile">
          <div className="user-profile-img">
            <Avatar size="40" name={this.state.firstName} src="" />
          </div>

          <div>
            <p>{this.state.username}</p>
            <small>Public</small>
            <p></p>
          </div>
        </div>

        <div className="post-input-container">
          <textarea rows="3" placeholder={"What's on your mind?"}></textarea>
          <div className="add-post-links">
            <a>Hashtags</a>
            <TagsInput
              color="blue"
              size="20"
              value={this.state.tags}
              onChange={this.handleChangeTags}
            />
            <br />
            <a>Mention</a>
            <Select
              isMulti
              name="colors"
              options={this.state.following}
              className="basic-multi-select"
              classNamePrefix="select"
            />
          </div>
          <button type="button" id="post-btn" onClick={this.createPost}>
            Post it
          </button>
        </div>
      </div>
    );
  }
}

export default WritePostComponent;
