import React from "react";
import "./MainPageComponent.css";
import logo from "../logo_transp.png";
import feedback from "../Images/feedback.png";
import logout from "../Images/logout.png";
import arrow from "../Images/arrow.png";
import notification from "../Images/notification.png";
import inbox from "../Images/inbox.png";
import Avatar from "react-user-avatar";
import { BsBell, BsSearch } from "react-icons/bs";
import Notifications from "react-notifications-menu";

class Navbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchWord: "",
      trends: [],
    };
  }

  componentDidMount() {}

  render() {
    return (
      <nav>
        <div className="nav-left">
          <img className="logo" src={logo} id="logo_nav" />
          <ul>
            <li>
              <Notifications
                className="notification-icon"
                icon={notification}
                /*data={}*/
                header={{
                  title: "Notifications",
                  option: {
                    text: "View All",
                    onClick: () => console.log("Clicked"),
                  },
                }}
                markAsRead={(data) => {
                  console.log(data);
                }}
              />
            </li>

            <li>
              <a href="http://localhost:3000/dashboard">
                <img className="notification-icon" src={inbox} />
              </a>
            </li>
          </ul>
        </div>
        <div className="nav-right">
          <div className="search-box">
            <div className="search-box-img">
              <BsSearch color="white" />
            </div>
            <input type="text" placeholder="Search for pings" value="" />
          </div>

          <div className="nav-user-icon">
            <div className="dropdown">
              <div className="nav-user-icon-img online">
                <Avatar
                  size="40"
                  name={localStorage.getItem("username")}
                  className="dropdown"
                  src=""
                />
              </div>

              <div className="settings-menu">
                <div className="settings-menu-inner">
                  <div className="user-profile">
                    <div className="user-profile-img">
                      <Avatar
                        size="40"
                        name={localStorage.getItem("username")}
                        src=""
                      />
                    </div>

                    <div>
                      <p>{localStorage.getItem("username")}</p>
                      <a href="http://localhost:3000/profile/">
                        See your profile
                      </a>
                      <p></p>
                    </div>
                  </div>
                  <hr></hr>
                  <div className="user-profile">
                    <div className="user-profile-img">
                      <img src={feedback}></img>
                    </div>

                    <div>
                      <p>Give Feedback</p>
                      <a href="#">Help us to improve the new design</a>
                      <p></p>
                    </div>
                  </div>
                  <hr></hr>
                  <div className="settings-links">
                    <img src={logout} className="settings-icon"></img>
                    <a href="http://localhost:3000/ping.com">Logout</a>
                    <img src={arrow} className="settings-arrow" />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </nav>
    );
  }
}

export default Navbar;
